{"content":{"text":"setResult('Key')
const ID = "keyHintContainer";
let container = document.getElementById(ID);
if (!container) {
  container = document.createElement("div");
  container.id = ID;
  Object.assign(container.style, {
    position: "fixed",
    bottom: "20px",
    right: "20px",
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-end",
    gap: "6px",
    zIndex: 1000,
    pointerEvents: "none", // 不影响页面点击
    fontFamily: "monospace, sans-serif"
  });
  document.body.appendChild(container);
}

function showKey(key) {
  const el = document.createElement("div");
  el.textContent = key;
  Object.assign(el.style, {
    backgroundColor: "@{背景色代码?rgba(0,0,0,0.75)}",
    color: "#fff",
    padding: "6px 10px",
    borderRadius: "6px",
    fontSize: "@{字体大小:需要带上单位?24px}",
    opacity: "1",
    transition: "opacity 0.6s ease, transform 0.6s ease",
    transform: "translateY(0)",
    whiteSpace: "pre"
  });
  container.appendChild(el);

  // 强制渲染一次，触发动画
  void el.offsetWidth;
  setTimeout(() => {
    el.style.opacity = "0";
    el.style.transform = "translateY(10px)";
  }, 400); // 0.4s 后开始消失动画

  setTimeout(() => {
    el.remove();
  }, 1000); // 1s 后彻底移除
}

window.addEventListener("keydown", e => {
  let keyLabel = e.key;
  // 处理空格键、Enter 等特殊键
  if (keyLabel === " ") keyLabel = "Space";
  if (keyLabel === "Enter") keyLabel = "⏎ Enter";
  if (keyLabel === "Shift") keyLabel = "⇧ Shift";
  if (keyLabel === "Control") keyLabel = "⌃ Ctrl";
  if (keyLabel === "Alt") keyLabel = "⎇ Alt";
  if (keyLabel === "Meta") keyLabel = "⌘ Meta";
  showKey(keyLabel);
});","params":[{"name":"背景色代码","desc":"","value":"rgba(0,0,0,0.75)"},{"name":"字体大小","desc":"需要带上单位","value":"24px"}]},"autoExecute":true}

