{"text":"<!doctype html>
<html lang="zh-CN">
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width,initial-scale=1" />
  <title>时分秒动态时钟</title>
  <style>
    html,body{
      height:100%;
      margin:0;
      background:transparent;
      display:flex;
      align-items:center; /* 垂直居中 */
      justify-content:center; /* 水平居中 */
    }

    .clock-wrap{
      -webkit-user-select:none;
      user-select:none;
    }

    .time{
      font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, "Roboto Mono", "SF Pro Text", "Segoe UI Mono", "Noto Sans Mono", monospace;
      font-variant-numeric: tabular-nums;
      line-height:1;
      letter-spacing:0.02em;
      color: @{字体颜色:请输入字体颜色代码?#fff};
      font-weight:600;
      font-size: var(--size, 64px);
      background: rgba(255,255,255,0);
      padding:0.1em 0.25em;
      border-radius:0.25em;
      text-align:center;
    }

    @media (max-width:420px){
      .time{ font-weight:600; }
    }
  </style>
</head>
<body>
  <div class="clock-wrap">
    <div class="time" id="clock" aria-live="polite" role="text">--:--:--</div>
  </div>

  <script>
    (function(){
      const el = document.getElementById('clock');
      const pad2 = (n) => (n < 10 ? '0' + n : String(n));

      function renderNow(date){
        const h = pad2(date.getHours());
        const m = pad2(date.getMinutes());
        const s = pad2(date.getSeconds());
        el.textContent = h + ':' + m + ':' + s;
      }

      function startClock(){
        const now = new Date();
        renderNow(now);
        const msToNextSecond = 1000 - now.getMilliseconds();
        setTimeout(() => {
          renderNow(new Date());
          const timer = setInterval(() => renderNow(new Date()), 1000);
          document.addEventListener('visibilitychange', () => {
            if (!document.hidden) {
              clearInterval(timer);
              const now2 = new Date();
              renderNow(now2);
              const next = 1000 - now2.getMilliseconds();
              setTimeout(() => {
                renderNow(new Date());
                setInterval(() => renderNow(new Date()), 1000);
              }, next);
            }
          });
        }, msToNextSecond);
      }

      function setFontSize(){
        const w = window.innerWidth;
        const h = window.innerHeight;
        // 根据宽度和高度同时考虑，取一个较合适的值
        const px = Math.max(18, Math.floor(Math.min(w * 0.18, h * 0.6)));
        document.documentElement.style.setProperty('--size', px + 'px');
      }

      window.addEventListener('resize', setFontSize, {passive:true});
      window.matchMedia('(resolution)').addEventListener?.('change', setFontSize);

      setFontSize();
      startClock();

      window.addEventListener('keydown', (e) => {
        if (e.key === 'w') el.style.color = '#fff';
        if (e.key === 'b') el.style.color = '#000';
        if (e.key === 'g') el.style.color = '#bdbdbd';
      });

    })();
  </script>
</body>
</html>","params":[{"name":"字体颜色","desc":"请输入字体颜色代码","value":"#fff"}]}
