style:
.result {
    align-items: center;
    display: flex;
    justify-content: center;
}

.link-block {
    text-align: center;
    text-decoration: none;
    padding: 8px;
    border-radius: 8px;
    transition: background-color 0.3s ease;
    display: flex;
    flex-direction: column;
    align-items: center;
    width: fit-content;
}

.link-block:hover {
    background-color: #ffffff24;
}

.link-image {
    width: 40px;
    height: 40px;
    border-radius: 6px;
    object-fit: cover;
    margin-bottom: 8px;
}

.link-name {
    color: #333;
    font-size: 16px;
    font-weight: bold;
}
