style:
.article-bottom {
  border-radius: 12px;
  overflow: hidden;
  background: #fff;
  height: 100%;
}

.article-bottom .content {
  padding: 25px;
  font-size: 15px;
  line-height: 1.9;
  color: #444;
  white-space: pre-line;
  overflow: auto;
  height: calc(100% - 98px);
}

.article-bottom .title {
  background: #2ecc71;
  color: #fff;
  font-weight: bold;
  font-size: 18px;
  text-align: center;
  padding: 12px;
}
