style:
.article-sideblock {
  display: grid;
  grid-template-columns: 100px 1fr;
  border-radius: 10px;
  overflow: hidden;
  min-width: 200px;
  height: 100%;
}

.article-sideblock .title {
  background: #2575fc;
  color: #fff;
  font-size: 18px;
  font-weight: bold;
  padding: 20px;
  writing-mode: vertical-rl;
  height: calc(100% - 40px);
  overflow: auto;
  letter-spacing: 2px;
}

.article-sideblock .content {
  background: #fff;
  font-size: 15px;
  color: #333;
  line-height: 1.8;
  padding: 25px;
  white-space: pre-line;
  height: 100%;
  overflow: auto;
}
