style:
.article-split {
  border-radius: 10px;
  overflow: hidden;
  height: 100%;
}

.article-split .title {
  background: #ff6b6b;
  color: #fff;
  font-size: 20px;
  font-weight: bold;
  padding: 15px 20px;
}

.article-split .content {
  background: #f9f9f9;
  height: calc(100% - 96px);
  color: #333;
  font-size: 15px;
  line-height: 1.8;
  padding: 20px;
  white-space: pre-line;
  overflow: auto;
}
