style:
.result {
  display: flex;
  align-items: center;
  justify-content: center;

  .underline-link {
    text-decoration: none;
    color: #3498db;
    font-weight: bold;
    position: relative;
  }

  .underline-link::after {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    left: 0;
    bottom: -3px;
    background: #3498db;
    transition: width 0.3s;
  }

  .underline-link:hover::after {
    width: 100%;
  }
}
