body {
  margin: 0;

  /* 背景渐变 */
  background: radial-gradient(circle at center, #343496 0%, #372727 100%);
  font-family: "Segoe UI", Arial, sans-serif;
}

/* 动态流光 */
body::before {
  content: "";
  position: absolute;
  top: -50%; left: -50%;
  width: 200%; height: 200%;
  background: conic-gradient(from 0deg, #0ff, #7928ca, #2af598, #009efd, #0ff);
  opacity: 0.5;
  animation: flowRotate 30s linear infinite;
  filter: blur(150px);
}

/* 流光旋转 */
@keyframes flowRotate {
  from { transform: rotate(0deg); }
  to   { transform: rotate(360deg); }
}

globalStyle.globalStyle-tag-文本框边框:
.textContent {
  border: 2px dashed white;
  height: calc(100% - 4px);
  width: calc(100% - 4px);
}

