body::before {
  content: "";
  position: absolute;
  top: -10%;
  left: 0;
  width: 100%;
  height: 20%; /* 扫描线高度 */
  background: linear-gradient(to bottom, #ffffff00, #ffffff1a, #ffffff2a, #ffffff3a);
  animation: scan 10s linear infinite;
}

/* 扫描动画 */
@keyframes scan {
  0%   { top: -10%; }
  100% { top: 100%; }
}

