<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>图片拖拽上传</title>
    <!-- 引入Tailwind CSS -->
    <script src="https://cdn.tailwindcss.com"></script>
    <!-- 引入Font Awesome -->
    <link href="https://cdn.jsdelivr.net/npm/font-awesome@4.7.0/css/font-awesome.min.css" rel="stylesheet">

    <!-- 配置Tailwind -->
    <script>
        tailwind.config = {
            theme: {
                extend: {
                extend: {
                    colors: {
                        primary: '#3B82F6',
                        secondary: '#10B981',
                        neutral: '#64748B',
                    },
                    fontFamily: {
                        inter: ['Inter', 'system-ui', 'sans-serif'],
                    },
                }
            }
        }
    </script>

    <!-- 自定义工具类 -->
    <style type="text/tailwindcss">
        @layer utilities {
            .content-auto {
                content-visibility: auto;
            }
            .backdrop-blur-xl {
                backdrop-filter: blur(24px);
            }
        }
    </style>

    <style>
        /* 基础样式 */
        body {
            font-family: 'Inter', system-ui, sans-serif;
            overflow-x: hidden;
        }

        /* 拖拽区域样式 */
        #dropArea {
            cursor: pointer;
            transition: all 0.3s ease;
        }

        #dropArea.drag-over {
            background-color: rgba(59, 130, 246, 0.15);
            border-color: #3B82F6;
        }

        /* 图片容器样式 */
        #imageContainer {
            opacity: 0;
            transform: translateY(20px);
            transition: opacity 0.5s ease, transform 0.5s ease;
            pointer-events: none; /* 防止点击穿透 */
        }

        #imageContainer.visible {
            opacity: 1;
            transform: translateY(0);
            pointer-events: auto;
        }

        /* 上传的图片样式 */
        #uploadedImage {
            max-width: 90%;
            max-height: 80vh;
            object-fit: contain;
            box-shadow: 0 10px 50px rgba(0, 0, 0, 0.15);
            transition: all 0.3s ease;
        }

        #uploadedImage:hover {
            transform: scale(1.02);
            box-shadow: 0 15px 60px rgba(0, 0, 0, 0.2);
        }

        /* 加载动画 */
        @keyframes spin {
            to { transform: rotate(360deg); }
        }

        .spinner {
            animation: spin 1s linear infinite;
        }

        /* 按钮样式优化 */
        #selectButton {
            pointer-events: auto !important;
        }
    </style></style>
</head>
<body class="bg-gray-100 min-h-screen flex flex-col">
    <!-- 顶部导航 -->
    <header class="bg-white/80 backdrop-blur-xl shadow-sm py-4 px-6 fixed top-0 left-0 right-0 z-50 transition-all duration-300">
        <div class="container mx-auto flex justify-between items-center">
            <h1 class="text-[clamp(1.25rem,3vw,1.75rem)] font-bold text-gray-800 flex items-center">
                <i class="fa fa-picture-o text-primary mr-2"></i>
                <span>图片上传器</span>
            </h1>
            <button id="resetBtn" class="hidden items-center px-4 py-2 bg-gray-200 hover:bg-gray-300 text-gray-700 rounded-lg transition-all duration-200">
                <i class="fa fa-refresh mr-2"></i>重新上传
            </button>
        </div>
    </header>

    <!-- 主内容区 -->
    <main class="flex-grow flex items-center justify-center p-4 pt-24 pb-16 relative overflow-hidden">
        <!-- 背景模糊层 - 初始隐藏 -->
        <div id="bgBlur" class="absolute inset-0 bg-cover bg-center opacity-0 transition-opacity duration-500"></div>

        <!-- 拖拽上传区域 -->
        <div id="dropArea" class="border-2 border-dashed border-gray-300 rounded-2xl p-10 w-full max-w-3xl text-center bg-white/50 backdrop-blur-xl shadow-md hover:border-primary/70 transition-all duration-300">
            <div class="flex flex-col items-center justify-center">
                <div class="w-20 h-20 mb-6 rounded-full bg-primary/10 flex items-center justify-center text-primary">
                    <i class="fa fa-cloud-upload text-4xl"></i>
                </div>
                <h2 class="text-[clamp(1.1rem,2vw,1.5rem)] font-semibold text-gray-800 mb-2">拖拽图片到此处上传</h2>
                <p class="text-gray-500 mb-6 max-w-md">或者点击下方按钮选择图片文件，支持JPG、PNG、GIF等常见格式</p>

                <!-- 上传按钮 -->
                <label for="fileInput" id="selectButton" class="px-6 py-3 bg-primary text-white rounded-lg font-medium cursor-pointer hover:bg-primary/90 active:bg-primary/80 transition-all duration-200 flex items-center">
                    <i class="fa fa-folder-open mr-2"></i>选择图片
                </label>
                <input type="file" id="fileInput" accept="image/*" class="hidden">

                <!-- 加载指示器 - 初始隐藏 -->
                <div id="loadingIndicator" class="hidden mt-6">
                    <i class="fa fa-circle-o-notch spinner text-primary text-2xl"></i>
                    <p class="mt-2 text-gray-600">正在处理图片...</p>
                </div>
            </div>
        </div>

        <!-- 图片显示区域 - 初始隐藏 -->
        <div id="imageContainer" class="absolute inset-0 flex flex-col items-center justify-center p-4">
            <img id="uploadedImage" src="" alt="上传的图片" class="rounded-lg">
            <p id="imageInfo" class="mt-4 text-gray-600 bg-white/80 backdrop-blur-xl px-4 py-2 rounded-full text-sm"></p>
        </div>
    </main>

    <!-- 页脚 -->
    <footer class="bg-white/80 backdrop-blur-xl py-4 px-6 text-center text-gray-500 text-sm shadow-inner">
        <p>支持拖拽上传 | 自动适应窗口 | 保持图片比例</p>
    </footer>

    <script>
        // 获取DOM元素
        const dropArea = document.getElementById('dropArea');
        const fileInput = document.getElementById('fileInput');
        const selectButton = document.getElementById('selectButton');
        const imageContainer = document.getElementById('imageContainer');
        const uploadedImage = document.getElementById('uploadedImage');
        const imageInfo = document.getElementById('imageInfo');
        const loadingIndicator = document.getElementById('loadingIndicator');
        const bgBlur = document.getElementById('bgBlur');
        const resetBtn = document.getElementById('resetBtn');

        // 拖拽事件处理
        ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
            dropArea.addEventListener(eventName, preventDefaults, false);
        });

        function preventDefaults(e) {
            e.preventDefault();
            e.stopPropagation();
        }

        ['dragenter', 'dragover'].forEach(eventName => {
            dropArea.addEventListener(eventName, highlight, false);
        });

        ['dragleave', 'drop'].forEach(eventName => {
            dropArea.addEventListener(eventName, unhighlight, false);
        });

        function highlight() {
            dropArea.classList.add('drag-over');
        }

        function unhighlight() {
            dropArea.classList.remove('drag-over');
        }

        // 处理文件上传
        dropArea.addEventListener('drop', handleDrop, false);
        fileInput.addEventListener('change', handleFiles, false);
        resetBtn.addEventListener('click', resetUpload, false);

        // 按钮点击事件单独处理，避免与拖拽冲突
        selectButton.addEventListener('click', (e) => {
            e.stopPropagation(); // 阻止事件冒泡
            fileInput.click();
        });

        // 阻止点击上传区域触发文件选择（只通过按钮）
        dropArea.addEventListener('click', (e) => {
            // 只有当点击的是按钮本身时才触发文件选择
            if (e.target !== selectButton && !selectButton.contains(e.target)) {
                e.preventDefault();
                return false;
            }
        });

        function handleDrop(e) {
            const dt = e.dataTransfer;
            const files = dt.files;
            handleImageFiles(files);
        }

        function handleFiles() {
            handleImageFiles(this.files);
        }

        function handleImageFiles(files) {
            if (!files || files.length === 0) return;

            const file = files[0];

            // 检查是否为图片文件
            if (!file.type.match('image.*')) {
                alert('请上传图片文件！');
                return;
            }

            // 显示加载指示器
            dropArea.classList.add('opacity-50');
            loadingIndicator.classList.remove('hidden');
            loadingIndicator.classList.add('flex', 'flex-col', 'items-center');

            // 读取图片文件
            const reader = new FileReader();

            reader.onload = function(e) {
                // 模拟处理延迟
                setTimeout(() => {
                    // 隐藏加载指示器
                    loadingIndicator.classList.add('hidden');
                    loadingIndicator.classList.remove('flex', 'flex-col', 'items-center');

                    // 设置图片源
                    uploadedImage.src = e.target.result;

                    // 显示图片信息
                    const sizeKB = (file.size / 1024).toFixed(1);
                    uploadedImage.onload = function() {
                        const dimensions = `${this.naturalWidth} × ${this.naturalHeight} px`;
                        imageInfo.textContent = `${file.name} · ${dimensions} · ${sizeKB} KB`;
                    };

                    // 设置背景模糊效果
                    bgBlur.style.backgroundImage = `url(${e.target.result})`;
                    bgBlur.style.opacity = '0.2';

                    // 隐藏上传区域，显示图片
                    dropArea.classList.add('hidden');
                    imageContainer.classList.add('visible');

                    // 显示重置按钮
                    resetBtn.classList.remove('hidden');
                    resetBtn.classList.add('flex');
                }, 600);
            }

            reader.readAsDataURL(file);
        }

        // 重置上传状态
        function resetUpload() {
            // 隐藏图片容器和重置按钮
            imageContainer.classList.remove('visible');
            resetBtn.classList.add('hidden');
            resetBtn.classList.remove('flex');

            // 重置背景模糊
            bgBlur.style.opacity = '0';
            setTimeout(() => {
                bgBlur.style.backgroundImage = 'none';
            }, 500);

            // 重置文件输入
            fileInput.value = '';

            // 显示上传区域
            setTimeout(() => {
                dropArea.classList.remove('hidden', 'opacity-50');
            }, 300);
        }

        // 窗口大小变化时重新调整图片尺寸
        window.addEventListener('resize', () => {
            if (uploadedImage.src) {
                // 触发重绘确保图片正确适应
                uploadedImage.style.display = 'none';
                uploadedImage.offsetHeight; // 触发重排
                uploadedImage.style.display = '';
            }
        });
    </script>
</body>
</html>
