<style>
.effect-container {
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
}

.glowing-box {
  position: relative;
  width: 200px;
  height: 200px;
  border-radius: 50%;
  background: linear-gradient(45deg, #ff0057, #4285f4, #0f9d58, #f4b400);
  animation: rotate 4s linear infinite;
}

/* 发光效果 */
.glowing-box::before {
  content: '';
  position: absolute;
  top: -5px;
  left: -5px;
  right: -5px;
  bottom: -5px;
  background: inherit;
  border-radius: 50%;
  filter: blur(30px);
  opacity: 0.7;
  z-index: -1;
}

/* 内部装饰环 */
.glowing-box::after {
  content: '';
  position: absolute;
  top: 20px;
  left: 20px;
  right: 20px;
  bottom: 20px;
  background: #ffffff;
  border-radius: 50%;
}

.glowing-box:hover {
  & {
    scale: 1.2;
  }
}

/* 旋转动画 */
@keyframes rotate {
    0% {
        background-position: 0% 50%;
        transform: rotate(0deg);
    }
    50% {
        background-position: 100% 50%;
    }
    100% {
        background-position: 0% 50%;
        transform: rotate(360deg);
    }
}
</style>
