.responsive-image {
    width: 100%;
    height: auto;
    object-fit: contain;
    max-height: 80vh;
    display: block;
    margin: 0 auto;
    border-radius: 100%;
}

.project-intro {
    width: calc(100% - 24px);
    padding: 25px;
    margin: 8px;
    border-radius: 8px;
    background-color: #ffffff3d;
    border: 4px solid #ffffff1a;
    transition: all 0.3s ease;
    box-sizing: border-box;
}

/* 鼠标悬停效果 */
.project-intro:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.08);
    border-color: #d0e1ff;
}

/* 标题样式 */
.project-title {
    margin: 0 0 15px 0;
    font-size: 1.5rem;
    color: #1a365d;
    transition: color 0.3s ease;
}

.project-intro:hover .project-title {
    color: #2563eb;
}

/* 描述样式 */
.project-description {
    margin: 0;
    font-size: 1rem;
    line-height: 1.6;
    color: #4b5563;
    transition: color 0.3s ease;
}

.project-intro:hover .project-description {
    color: #1f2937;
}

/* 响应式字体调整 */
@media (max-width: 768px) {
    .project-title {
        font-size: 1.3rem;
    }

    .project-description {
        font-size: 0.95rem;
    }
}

