globalStyle.globalStyle-tag-介绍文字样式:
.textContent {
  .result {
    display: flex;
    flex-direction: column;

    .title {
      font-size: 24px;
      font-weight: bold;
      margin-bottom: 4px;
      color: white;
    }

    .desc {
      color: #e3e3e3;
      font-size: 16px;
    }
  }
}

globalStyle.globalStyle-tag-图片样式:
.image-container {
    width: 100%;
    max-width: 800px;
    margin: 0 auto;
    padding: 10px;
    box-sizing: border-box;
}

.responsive-image {
    width: 100%;
    height: auto;
    object-fit: contain;
    max-height: 80vh;
    display: block;
    margin: 0 auto;
}

components.style:
.button {
  background-color: unset !important;

  svg {
    width: 60%;
    fill: white;

    &:hover {
       transform: rotate(180deg);
    }
  }
}

