<html lang="zh-CN">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>随机数字抽取</title>
    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            scrollbar-width: none;
        }

        body {
            min-height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
            font-family: 'Arial', sans-serif;
        }

        #number-display {
            font-size: 50vw;
            font-weight: bold;
            cursor: pointer;
            transition: all 0.3s ease;
            position: relative;
            color: #333;
        }

        #number-display.highlight {
            animation: highlight 1s ease-out forwards;
        }

        @keyframes highlight {
            0% {
                transform: scale(1);
                text-shadow: 0 0 20px rgba(255, 100, 0, 0.5);
            }

            50% {
                transform: scale(1.5);
                text-shadow: 0 0 40px rgba(255, 100, 0, 0.8);
            }

            100% {
                transform: scale(1);
                text-shadow: 0 0 30px rgba(255, 100, 0, 0.7);
            }
        }
    </style>
</head>

<body>
    <div id="number-display">0</div>

    <script>
        const numberDisplay = document.getElementById('number-display');
        let isAnimating = false;
        let animationFrameId;
        let startTime;
        const duration = 3000; // 进一步延长动画总时长
        const slowdownStart = 0.5; // 从50%进度就开始减速（之前是70%）

        numberDisplay.addEventListener('click', startAnimation);

        function startAnimation() {
            if (isAnimating) return;

            isAnimating = true;
            numberDisplay.classList.remove('highlight');
            startTime = Date.now();
            animate();
        }

        function animate() {
            const elapsed = Date.now() - startTime;
            const progress = Math.min(elapsed / duration, 1);

            // 使用更明显的减速缓动函数
            const easeProgress = easeOutExpo(progress);
            const currentNumber = Math.floor(Math.random() * 100);
            numberDisplay.textContent = currentNumber;

            if (progress < 1) {
                // 重新设计速度曲线，使减速更明显
                let speed;
                if (progress < slowdownStart) {
                    // 加速阶段：快速达到最高速度
                    speed = 100 - (progress / slowdownStart) * 90;
                } else {
                    // 减速阶段：使用指数曲线缓慢减速
                    const slowdownProgress = (progress - slowdownStart) / (1 - slowdownStart);
                    // 指数函数使减速越来越慢
                    speed = 10 + Math.pow(slowdownProgress, 0.3) * 500;
                }
                speed = Math.max(50, Math.min(speed, 100));
                animationFrameId = setTimeout(animate, speed);
            } else {
                // 添加短暂延迟后显示最终结果，增强停止感
                setTimeout(() => {
                    const finalNumber = Math.floor(Math.random() * 100);
                    numberDisplay.textContent = finalNumber;
                    numberDisplay.classList.add('highlight');
                    isAnimating = false;
                }, 200);
            }
        }

        // 更明显的减速缓动函数
        function easeOutExpo(t) {
            return t === 1 ? 1 : 1 - Math.pow(2, -10 * t);
        }

    </script>
</body>

</html>"}
